--DROP TABLE "WEBMENU";

CREATE TABLE "WEBMENU" (
        "ID" INTEGER GENERATED BY DEFAULT AS IDENTITY (
                START WITH 1
                INCREMENT BY 1
                MINVALUE 1
                MAXVALUE 2147483647
                NO CYCLE
                CACHE 20
                NO ORDER ),
        "NAME" VARCHAR(50) CCSID 284 NOT NULL,
        "URL" VARCHAR(100) CCSID 284 NOT NULL,
        "PGM" VARCHAR(50) CCSID 284 DEFAULT NULL,
        "PARENTID" INTEGER,
        "ORDERS" INTEGER NOT NULL,
        "STATUSID" INTEGER NOT NULL,
        "ISMENU" INTEGER NOT NULL
)
GO